import Admin_nav from "../components/Admin_nav";
import React, { useState, useEffect } from "react";
import HeaderNav from "../components/HeaderNav";
import ListMandobes from "../components/ListMandobes";

import PersonNeeds from "../components/PersonNeeds";
import ButtonUploadImage from "../components/ButtonUpload";
import CounterInput from "react-counter-input";

import api from "../services/Api";

import Footer from "../components/Footer";
import { GetFullCCAs } from "../services/api_get";
import useAuth, { ProtectRoute } from "../contexts/auth.js";
import { useAlert } from "react-alert";
import Router from "next/router";

import Cookies from "js-cookie";

import axios from "axios";

function Table() {
  const alert = useAlert();

  //const [project,setProject] = useState(false);

  const [SelectedMembers, setSelectedMembers] = useState([
    {
      Member_id: 99999999999,
      Member_Name: "",
      Member_img: false,
      Member_Number: "",
    },
  ]);

  const { toggler, settoggler, loading } = useAuth();
  const [spinner, setspinner] = useState(false);
  const [NewMandobe_id, setNewMandobe_id] = useState(0);
  const [pagedown, setpagedown] = useState(0);

  console.log(NewMandobe_id);

  const { data, isLoading, isError } = GetFullCCAs();

  const results = isLoading ? false : data.data;
  console.log(results);

  const showSkeleton = isLoading || loading;
  const [CCAs, setCCAs] = useState([]);

  const [citys, setcitys] = useState([]);
  const [aeras, setaeras] = useState([]);
  const [des, setdes] = useState("");

  const [TheSelectedCountry, setTheSelectedCountry] = useState("");
  const [TheSelectedCity, setTheSelectedCity] = useState("");
  const [TheSelectedaera, setTheSelectedaera] = useState("all");
  const [firstTime, setfirstTime] = useState(true);
  const [categories, setcategories] = useState([]);

  if (firstTime && results) {
    setCCAs(results.options.CCAs);

    setTheSelectedCity(results.options.CCAs[0].data[0].name);

    setTheSelectedCountry(results.options.CCAs[0].name);

    setcitys(results.options.CCAs[0].data);
    setaeras(results.options.CCAs[0].data[0].data);
    setcategories(results.options.categories);

    setfirstTime(false);
  }

  const [familyType, setfamilyType] = useState("family");
  const [priority, setpriority] = useState("normal");
  const [FirstName, setFirstName] = useState("");
  const [LastName, setLastName] = useState("");
  const [email, setemail] = useState("");
  const [phone, setphone] = useState("");
  const [mempersCount, setmempersCount] = useState(0);
  const [share, setshare] = useState(0);

  const [oldWoman, setOldWoman] = useState(0);
  const [oldMan, setOldMan] = useState(0);
  const [accibilityPerson, setAccibilityPerson] = useState(0);
  const [baby, setBaby] = useState(0);
  const [orphanGirls, setOrphanGirls] = useState(0);
  const [orphanBoys, setOrphanBoys] = useState(0);
  const [girls, setGirls] = useState(0);
  const [guys, setGuys] = useState(0);
  const [women, setWomen] = useState(0);
  const [men, setMen] = useState(0);
  const [widows, setWidows] = useState(0);
  const [girl, setgirl] = useState(0);
  const [boy, setboy] = useState(0);
  const [PhoneField, setPhoneField] = useState();

  const [address, setaddress] = useState("");

  const [personNeeds, setPersonNeeds] = useState("");

  const [step, setStep] = useState(1);
  const [list, updateList] = useState([]);
  const [allowed, setallowed] = useState(false);

  function updateArray(data) {
    let favorite = list;
    favorite.push(data);

    updateList(favorite);
    setallowed(true);

    //  setPersonNeeds((PersonNeeds) => PersonNeeds.concat(data));
    // setPersonNeeds((PersonNeeds) => [...PersonNeeds, data]);
  }

  async function removeUser(id) {
    let favorite = list;
    if (list.length == 1) {
      setallowed(false);
    }
    favorite = await favorite.filter((e) => e.id !== id);
    updateList(favorite);
  }

  useEffect(() => {
    recalc();
  }, [
    oldWoman,
    oldMan,
    accibilityPerson,
    baby,
    orphanGirls,
    orphanBoys,
    widows,
    girls,
    guys,
    women,
    men,
    girl,
    boy,
  ]);

  function mysubmit() {
    let checke = true;
    let mycalc =
      0 +
      parseInt(oldWoman) +
      parseInt(oldMan) +
      parseInt(accibilityPerson) +
      parseInt(baby) +
      parseInt(orphanGirls) +
      parseInt(orphanBoys) +
      parseInt(widows) +
      parseInt(girls) +
      parseInt(guys) +
      parseInt(women) +
      parseInt(men) +
      parseInt(girl) +
      parseInt(boy);
    console.log(mycalc);

    let data = {
      TheSelectedCity,
      TheSelectedaera,
      FirstName,
      LastName,
      email,
      phone,
      familyType,
      priority,
      oldWoman,
      oldMan,
      accibilityPerson,
      baby,
      orphanGirls,
      orphanBoys,
      widows,
      girls,
      guys,
      women,
      men,
      TheSelectedCountry,
      girl,
      boy,
      share,
      mempersCount: mycalc,

      categories: list,
      des,
      mandobe_id: NewMandobe_id,
      address,
      SelectedMembers,
    };

    console.log(data);
    setmempersCount(mycalc);
    if (mycalc == 0) {
      checke = false;
      alert.show(" الرجاء تحديد عدد الافراد ", {
        timeout: 2000,
        type: "error",
      });
    } else {
    }
    if (data.categories.length == 0) {
      checke = false;
      alert.show("الرجاء إختيار إحتياج واحد أو أكثر ", {
        timeout: 2000,
        type: "error",
      });
    }
    if (FirstName == "") {
      checke = false;
      alert.show("الرجاء كتابة الإسم الأول", {
        timeout: 2000,
        type: "error",
      });
    }

    if (address == "") {
      checke = false;
      alert.show("الرجاء كتابة عنوان المستفيد", {
        timeout: 2000,
        type: "error",
      });
    }

    if (NewMandobe_id == 0) {
      checke = false;
      alert.show("الرجاء إختيار مندوب", {
        timeout: 2000,
        type: "error",
      });
    }

    if (LastName == "") {
      checke = false;
      alert.show("الرجاء كتابة الإسم الاخير", {
        timeout: 2000,
        type: "error",
      });
    }

    if (phone == "") {
      checke = false;
      alert.show("الرجاء كتابة رقم الجوال", {
        timeout: 2000,
        type: "error",
      });
    }

    if (SelectedMembers.length < NumberOfmembers) {
      checke = false;
      alert.show("الرجاء إضافة هويات جميع أفراد العائلة", {
        timeout: 2000,
        type: "error",
      });
    }
    setspinner(true);
    if (checke) {
      api
        .post("api/CreatNewUser", data)
        .then((res) => {
          setspinner(false);
          console.log(res.data);

          if (res.data == "already exists") {
            alert.show("إسم الكوبون موجود مسبقا ", {
              timeout: 2000,
              type: "error",
            });
          }
          if (res.data == "phone") {
            alert.show("رقم الجوال مستخدم مسبقا", {
              timeout: 2000,
              type: "error",
            });
          }
          if (res.data == "email") {
            alert.show("الإيميل مستخدم مسبقا", {
              timeout: 2000,
              type: "error",
            });
          }
          if (res.data == "NotEmail") {
            alert.show("الرجاء إدخال إيميل صحيح", {
              timeout: 2000,
              type: "error",
            });
          }

          if (res.data > 0) {
            alert.show("تم بنجاح", {
              timeout: 2000,
              type: "success",
            });
            Router.push("/Users");
            //GoBackAndTriggerMutation();
            //router.push("/companys/[name]", "/companys/" + formdata.companyName);
          }
        })
        .catch((err) => {
          alert.show("حصل خطأ", {
            timeout: 2000,
            type: "error",
          });
          setspinner(false);
          console.log(err);
        });
    }
  }

  function ThePhone(value) {
    for (let index = 0; index < CCAs.length; index++) {
      if (CCAs[index].name == TheSelectedCountry) {
        let element = CCAs[index].code;
        setphone(element + "" + value);
        setPhoneField(value);
      } else {
        let element = "";
      }
    }
  }

  function testc() {
    let mycalc =
      0 +
      parseInt(oldWoman) +
      parseInt(oldMan) +
      parseInt(accibilityPerson) +
      parseInt(baby) +
      parseInt(orphanGirls) +
      parseInt(orphanBoys) +
      parseInt(widows) +
      parseInt(girls) +
      parseInt(guys) +
      parseInt(women) +
      parseInt(men) +
      parseInt(girl) +
      parseInt(boy);
    console.log("tttt");

    console.log(mycalc);
    return mycalc;
  }

  async function recalc() {
    let tt = await testc();

    console.log("tt");

    console.log(tt);

    setshare(tt);
  }

  function getNumberOfmembers() {
    let mycalc =
      0 +
      parseInt(oldWoman) +
      parseInt(oldMan) +
      parseInt(accibilityPerson) +
      parseInt(baby) +
      parseInt(orphanGirls) +
      parseInt(orphanBoys) +
      parseInt(widows) +
      parseInt(girls) +
      parseInt(guys) +
      parseInt(women) +
      parseInt(men) +
      parseInt(girl) +
      parseInt(boy);

    return mycalc;
  }

  const NumberOfmembers = getNumberOfmembers();

  function removeMeber(QMember) {
    let mydata = SelectedMembers;
    let newdata = [];
    for (let index = 0; index < mydata.length; index++) {
      const element = mydata[index];

      if (element.Member_id != QMember.Member_id) {
        newdata.push(element);
      }
    }
    setSelectedMembers(newdata);
    setpagedown(pagedown + 1);
  }

  function removeMember_img(QMember) {
    let mydata = SelectedMembers;
    let newdata = [];
    for (let index = 0; index < mydata.length; index++) {
      const element = mydata[index];

      if (element.Member_id == QMember.Member_id) {
        element.Member_img = "";
      }
      newdata.push(element);
    }
    setSelectedMembers(newdata);
  }

  function UpdateMembereName(QMember, NewName) {
    let mydata = SelectedMembers;
    let newdata = [];
    for (let index = 0; index < mydata.length; index++) {
      const element = mydata[index];

      if (element.Member_id == QMember.Member_id) {
        element.Member_Name = NewName;
      }
      newdata.push(element);
    }
    setSelectedMembers(newdata);
  }

  function UpdateMembereNumber(QMember, NewName) {
    let mydata = SelectedMembers;
    let newdata = [];
    for (let index = 0; index < mydata.length; index++) {
      const element = mydata[index];

      if (element.Member_id == QMember.Member_id) {
        element.Member_Number = NewName;
      }
      newdata.push(element);
    }
    setSelectedMembers(newdata);
  }

  let step2 = step;

  const token = Cookies.get("token");
  const siteUrl = Cookies.get("siteUrl");

  function NewMember() {
    let mydata = SelectedMembers;
    let random_id = Math.random();
    let newdata = {
      Member_id: random_id,
      Member_Name: "",
      Member_img: false,
      Member_Number: "",
    };

    mydata.push(newdata);

    setSelectedMembers(mydata);

    setpagedown(pagedown + 1);
  }

  function handleThumbnailProduct(file, QMember) {
    let formData = new FormData();
    let myimg = "";

    formData.append("file", file);

    axios
      .post(siteUrl + "wp/v2/media", formData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((result) => {
        console.log(result.data.source_url);
        myimg = result.data.source_url;
        //add it here

        let mydata = SelectedMembers;
        let newdata = [];
        for (let index = 0; index < mydata.length; index++) {
          const element = mydata[index];

          if (element.Member_id == QMember.Member_id) {
            element.Member_img = myimg;
          }
          newdata.push(element);
        }
        setSelectedMembers(newdata);
      });
  }

  return (
    <>
      <body className="rtl g-sidenav-show g-sidenav-pinned" dir="rtl">
        <div>
          {/* Sidenav */}
          <Admin_nav />
          {/* Main content */}

          <div class="main-content" id="panel">
            {/* Topnav */}
            <HeaderNav />
            {/* Header */}
            {/* Header */}

            <div class="container-fluid mt--6">
              <div class="row justify-content-center">
                <div class=" col ">
                  <div class="card">
                    <div class="card-header bg-transparent">
                      <h3 class="mb-0">تسجيل مستفيد جديد</h3>
                    </div>
                    <div class="card-body">
                      {/* Light table */}
                      {step == 1 ? (
                        <div>
                          <div className="col-lg-4">
                            <div className="form-group">
                              <label className="form-control-label">
                                الدولة
                              </label>
                              <select
                                name="county"
                                onChange={(e) => {
                                  let selectedCounty = e.target.value;
                                  setTheSelectedCountry(selectedCounty);
                                  /*if (firstTime) {
                                      setfirstTime(false);
                                    }*/
                                  for (
                                    let index = 0;
                                    index < CCAs.length;
                                    index++
                                  ) {
                                    const tempconunty = CCAs[index];
                                    if (tempconunty.name === selectedCounty) {
                                      setcitys(tempconunty.data);

                                      if (tempconunty.data[0]) {
                                        setTheSelectedCity(
                                          tempconunty.data[0].name
                                        );
                                        setaeras(tempconunty.data[0].data);
                                      } else {
                                        setaeras([]);
                                      }
                                    }
                                  }
                                }}
                                class="form-control"
                              >
                                {CCAs
                                  ? CCAs.map((county, index) => {
                                      return <option>{county.name}</option>;
                                    })
                                  : ""}
                              </select>
                            </div>
                          </div>
                          <div className="col-lg-4">
                            <div className="form-group">
                              <label className="form-control-label">
                                المدينة
                              </label>
                              <select
                                id="mycity"
                                name="city"
                                onChange={(e) => {
                                  let selectedcity = e.target.value;
                                  setTheSelectedCity(e.target.value);
                                  /*if (firstTime) {
                                      setfirstTime(false);
                                    }*/
                                  for (
                                    let index = 0;
                                    index < citys.length;
                                    index++
                                  ) {
                                    const tempcity = citys[index];
                                    if (tempcity.name === selectedcity) {
                                      setaeras(tempcity.data);
                                    }
                                  }
                                }}
                                class="form-control"
                              >
                                {citys.length > 0
                                  ? citys.map((city, index) => {
                                      return (
                                        <option value={city.name}>
                                          {city.name}
                                        </option>
                                      );
                                    })
                                  : ""}
                              </select>
                            </div>
                          </div>
                          <div className="col-lg-4">
                            <div className="form-group">
                              <label className="form-control-label">
                                المنطقة
                              </label>
                              <select
                                name="aeras"
                                onChange={(e) => {
                                  setTheSelectedaera(e.target.value);
                                }}
                                class="form-control"
                              >
                                <option value="0">الكل</option>
                                {aeras.length > 0
                                  ? aeras.map((aeras, index) => {
                                      return (
                                        <option value={aeras.name}>
                                          {aeras.name}
                                        </option>
                                      );
                                    })
                                  : ""}
                              </select>
                            </div>
                          </div>
                          <h6 className="heading-small text-muted mb-4">
                            معلومات المستفيد
                          </h6>
                          <div className="col-lg-6">
                            <div className="form-group">
                              <label className="form-control-label">
                                الإسم الأول
                              </label>
                              <input
                                type="text"
                                name="FirstName"
                                onChange={(e) => {
                                  setFirstName(e.target.value);
                                }}
                                value={FirstName}
                                className="form-control"
                                placeholder="الإسم الأول"
                              />
                            </div>
                          </div>
                          <div className="col-lg-6">
                            <div className="form-group">
                              <label className="form-control-label">
                                الإسم الإخير
                              </label>
                              <input
                                type="text"
                                name="LastName"
                                onChange={(e) => {
                                  setLastName(e.target.value);
                                }}
                                value={LastName}
                                className="form-control"
                                placeholder="الإسم الإخير"
                              />
                            </div>
                          </div>

                          <div className="col-lg-6">
                            <div className="form-group">
                              <label className="form-control-label">
                                رقم الجوال
                              </label>
                              <div class="input-group">
                                <input
                                  type="number"
                                  name="PhoneField"
                                  onChange={(e) => {
                                    ThePhone(e.target.value);
                                  }}
                                  value={PhoneField}
                                  className="form-control"
                                  placeholder="50xxxxx"
                                />
                                {TheSelectedCountry ? (
                                  <>
                                    {CCAs.length > 0
                                      ? CCAs.map((data, i) => {
                                          if (data.name == TheSelectedCountry) {
                                            return (
                                              <div class="input-group-prepend">
                                                <span class="input-group-text">
                                                  {data.code}+
                                                </span>
                                              </div>
                                            );
                                          }
                                        })
                                      : ""}
                                  </>
                                ) : (
                                  <div class="input-group-prepend">
                                    <span class="input-group-text">+</span>
                                  </div>
                                )}{" "}
                              </div>
                            </div>
                          </div>
                          <div className="col-lg-6">
                            <div className="form-group">
                              <label className="form-control-label">
                                العنوان
                              </label>
                              <input
                                type="text"
                                name="address"
                                onChange={(e) => {
                                  setaddress(e.target.value);
                                }}
                                value={address}
                                className="form-control"
                                placeholder="عنوان المستفيد"
                              />
                            </div>
                          </div>

                          <div className="col-lg-12">
                            <div className="form-group">
                              <label className="form-control-label">
                                وصف الحالة
                              </label>
                              <textarea
                                type="text"
                                name="des"
                                onChange={(e) => {
                                  setdes(e.target.value);
                                }}
                                value={des}
                                className="form-control"
                                rows="4"
                                placeholder=" وصف حالة المستفيد"
                              />
                            </div>
                          </div>
                          <div className="col-lg-12">
                            <hr />
                          </div>
                          <h3>نوع المحتاج:</h3>
                          <div style={{ marginRight: "15px" }}>
                            <div class="custom-control custom-radio mb-3">
                              <input
                                type="radio"
                                id="customRadio1"
                                name="customRadio"
                                class="custom-control-input"
                                onChange={(e) => {
                                  if (e.target.value == "on") {
                                    setfamilyType("family");
                                  }
                                }}
                                checked
                              />
                              <label
                                class="custom-control-label"
                                for="customRadio1"
                              >
                                عائلة
                              </label>
                            </div>
                            <div class="custom-control custom-radio">
                              <input
                                type="radio"
                                id="customRadio2"
                                name="customRadio"
                                class="custom-control-input"
                                onChange={(e) => {
                                  if (e.target.value == "on") {
                                    setfamilyType("single");
                                  }
                                }}
                              />
                              <label
