                                      setfirstTime(false);
                                    }*/
                                  for (
                                    let index = 0;
                                    index < CCAs.length;
                                    index++
                                  ) {
                                    const tempconunty = CCAs[index];
                                    if (tempconunty.name === selectedCounty) {
                                      setcitys(tempconunty.data);

                                      if (tempconunty.data[0]) {
                                        setTheSelectedCity(
                                          tempconunty.data[0].name
                                        );
                                        setaeras(tempconunty.data[0].data);
                                      } else {
                                        setaeras([]);
                                      }
                                    }
                                  }
                                }}
                                class="form-control"
                              >
                                {CCAs
                                  ? CCAs.map((county, index) => {
                                      return <option>{county.name}</option>;
                                    })
                                  : ""}
                              </select>
                            </div>
                          </div>

                          <div className="col-lg-4">
                            <div className="form-group">
                              <label className="form-control-label">
                                المدينة
                              </label>
                              <select
                                id="mycity"
                                name="city"
                                onChange={(e) => {
                                  let selectedcity = e.target.value;
                                  setTheSelectedCity(e.target.value);
                                  /*if (firstTime) {
                                      setfirstTime(false);
                                    }*/
                                  for (
                                    let index = 0;
                                    index < citys.length;
                                    index++
                                  ) {
                                    const tempcity = citys[index];
                                    if (tempcity.name === selectedcity) {
                                      setaeras(tempcity.data);
                                    }
                                  }
                                }}
                                class="form-control"
                              >
                                {citys.length > 0
                                  ? citys.map((city, index) => {
                                      return (
                                        <option value={city.name}>
                                          {city.name}
                                        </option>
                                      );
                                    })
                                  : ""}
                              </select>
                            </div>
                          </div>
                          <div className="col-lg-4">
                            <div className="form-group">
                              <label className="form-control-label">
                                المنطقة
                              </label>
                              <select
                                name="aeras"
                                onChange={(e) => {
                                  setTheSelectedaera(e.target.value);
                                }}
                                class="form-control"
                              >
                                {aeras.length > 0
                                  ? aeras.map((aeras, index) => {
                                      return (
                                        <option value={aeras.name}>
                                          {aeras.name}
                                        </option>
                                      );
                                    })
                                  : ""}
                              </select>
                            </div>
                          </div>
                          <h6 className="heading-small text-muted mb-4">
                            معلومات الجمعية
                          </h6>

                          <div className="col-lg-12">
                            <div className="form-group">
                              <label className="form-control-label">
                                الإسم
                              </label>
                              <input
                                type="text"
                                name="FirstName"
                                onChange={(e) => {
                                  setFirstName(e.target.value);
                                }}
                                value={FirstName}
                                className="form-control"
                                placeholder="الإسم "
                              />
                            </div>
                          </div>

                          <div className="col-lg-12">
                            <div className="form-group">
                              <label className="form-control-label">
                                رقم الجوال
                              </label>
                              <input
                                type="number"
                                name="phone"
                                onChange={(e) => {
                                  setphone(e.target.value);
                                }}
                                value={phone}
                                className="form-control"
                                placeholder="96650xxxxx"
                              />
                            </div>
                          </div>

                          <hr />

                          <hr />
                        </div>
                      ) : (
                        ""
                      )}

                      <div>
                        <div
                          style={{
                            margin: "0 auto",
                            textAlign: "center",
                            marginTop: "9em",
                          }}
                        >
                          <div style={{ textAlign: "left" }}>
                            <button
                              class="btn btn-icon btn-success"
                              onClick={() => {
                                mysubmit();
                              }}
                              type="button"
                            >
                              <span
                                style={{
                                  marginLeft: ".75em",
                                  marginRight: "auto",
                                }}
                                class="btn-inner--text"
                              >
                                إنشاء
                              </span>
                              {"     "}
                              <span class="btn-inner--icon">
                                <i class="ni ni-send"></i>
                              </span>
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <Footer />
            </div>
          </div>

          {/* Argon Scripts */}
          {/* Core */}
          {/* Optional JS */}
          {/* Argon JS */}
        </div>
      </body>
    </>
  );
